"use strict";(self.webpackChunkdocusaurus=self.webpackChunkdocusaurus||[]).push([[6394],{64072:(e,n,t)=>{t.r(n),t.d(n,{assets:()=>l,contentTitle:()=>a,default:()=>c,frontMatter:()=>r,metadata:()=>i,toc:()=>d});const i=JSON.parse('{"id":"dead-poets-society","title":"\u3010\u7535\u5f71\u3011\u6b7b\u4ea1\u8bd7\u793e\uff08Dead Poets Society\uff09","description":"\u7535\u5f71\u7b80\u4ecb","source":"@site/kb/movies/dead-poets-society.md","sourceDirName":".","slug":"/dead-poets-society","permalink":"/movies/dead-poets-society","draft":false,"unlisted":false,"tags":[],"version":"current","sidebarPosition":2,"frontMatter":{"sidebar_label":"\u3010\u7535\u5f71\u3011\u6b7b\u4ea1\u8bd7\u793e","sidebar_position":2},"sidebar":"tutorialSidebar","previous":{"title":"\u3010\u7535\u5f71\u3011\u8ffd\u68a6\u8d64\u5b50\u5fc3","permalink":"/movies/rudy-1993"},"next":{"title":"\u3010\u7eaa\u5f55\u7247\u3011\u5f92\u624b\u6500\u5ca9","permalink":"/movies/free-solo"}}');var o=t(74848),s=t(28453);const r={sidebar_label:"\u3010\u7535\u5f71\u3011\u6b7b\u4ea1\u8bd7\u793e",sidebar_position:2},a="\u3010\u7535\u5f71\u3011\u6b7b\u4ea1\u8bd7\u793e\uff08Dead Poets Society\uff09",l={},d=[{value:"\u7535\u5f71\u7b80\u4ecb",id:"\u7535\u5f71\u7b80\u4ecb",level:2},{value:"\u7ecf\u5178\u8bed\u5f55",id:"\u7ecf\u5178\u8bed\u5f55",level:2}];function h(e){const n={h1:"h1",h2:"h2",header:"header",li:"li",ol:"ol",p:"p",strong:"strong",...(0,s.R)(),...e.components};return(0,o.jsxs)(o.Fragment,{children:[(0,o.jsx)(n.header,{children:(0,o.jsx)(n.h1,{id:"\u7535\u5f71\u6b7b\u4ea1\u8bd7\u793edead-poets-society",children:"\u3010\u7535\u5f71\u3011\u6b7b\u4ea1\u8bd7\u793e\uff08Dead Poets Society\uff09"})}),"\n",(0,o.jsx)(n.h2,{id:"\u7535\u5f71\u7b80\u4ecb",children:"\u7535\u5f71\u7b80\u4ecb"}),"\n",(0,o.jsx)(n.p,{children:"\u300a\u6b7b\u4ea1\u8bd7\u793e\u300b\u662f\u7f8e\u56fd 1989 \u5e74\u4e0a\u6620\u7684\u4e00\u90e8\u5267\u60c5\u7535\u5f71\uff0c\u5f71\u7247\u5c55\u793a\u4e86\u5173\u4e8e\u5b88\u65e7\u4e0e\u521b\u65b0\u3001\u73b0\u5b9e\u4e0e\u6d6a\u6f2b\u3001\u65b0\u4eba\u4e0e\u8001\u4eba\u4e24\u80a1\u52bf\u529b\u7684\u8f83\u91cf\u3002\u6bcf\u6b21\u770b\u90fd\u4f1a\u52fe\u8d77\u6211\u5fc3\u4e2d\u7684\u6ce2\u6f9c\uff0c\u53cd\u601d\u73b0\u5728\u7684\u751f\u6d3b\uff0c\u53d8\u5f97\u6709\u529b\u91cf\u3002"}),"\n",(0,o.jsx)("iframe",{src:"//player.bilibili.com/player.html?aid=10112142&bvid=BV1rx411U7xi&cid=16708703&page=1&high_quality=1&danmaku=0",allowfullscreen:"allowfullscreen",width:"100%",height:"500",scrolling:"no",frameborder:"0",sandbox:"allow-top-navigation allow-same-origin allow-forms allow-scripts"}),"\n",(0,o.jsx)(n.h2,{id:"\u7ecf\u5178\u8bed\u5f55",children:"\u7ecf\u5178\u8bed\u5f55"}),"\n",(0,o.jsxs)(n.ol,{children:["\n",(0,o.jsx)(n.li,{children:"I went to the woods because I wanted to live deliberately... I wanted to live deep and suck out all the marrow of life! To put to rout all that was not life... And not when I came to die, discover that I had not lived...\n\u6211\u6b65\u5165\u4e1b\u6797\uff0c\u56e0\u4e3a\u6211\u5e0c\u671b\u6d3b\u5f97\u6709\u610f\u4e49... \u6211\u5e0c\u671b\u6d3b\u5f97\u6df1\u523b\uff0c\u6c72\u53d6\u751f\u547d\u6240\u6709\u7684\u7cbe\u9ad3\uff01\u628a\u975e\u751f\u547d\u7684\u4e00\u5207\u5168\u90fd\u51fb\u6e83... \u4ee5\u514d\u5728\u6211\u751f\u547d\u7ec8\u7ed3\u65f6\uff0c\u53d1\u73b0\u81ea\u5df1\u4ece\u6765\u6ca1\u6709\u6d3b\u8fc7..."}),"\n",(0,o.jsx)(n.li,{children:"We don't read and write poetry because it's cute.We read and write poetry because we are members of the human race.And the human race is filled with passion.And medicine,law,business,engineering,these are noble pursuits and necessary to sustain life.But poetry,beauty,romance,love,these are what we stay alive for.\n\u6211\u4eec\u8bfb\u8bd7\u5199\u8bd7\uff0c\u975e\u4e3a\u5b83\u7684\u7075\u5de7\u3002\u6211\u4eec\u8bfb\u8bd7\u5199\u8bd7\uff0c\u56e0\u4e3a\u6211\u4eec\u662f\u4eba\u7c7b\u7684\u4e00\u5458\u3002\u800c\u4eba\u7c7b\u5145\u6ee1\u4e86\u70ed\u60c5\u3002\u533b\u836f\uff0c\u6cd5\u5f8b\uff0c\u5546\u4e1a\uff0c\u5de5\u7a0b\uff0c\u8fd9\u4e9b\u90fd\u662f\u9ad8\u8d35\u7684\u7406\u60f3\uff0c\u5e76\u4e14\u662f\u7ef4\u751f\u7684\u5fc5\u9700\u6761\u4ef6\u3002\u4f46\u662f\u8bd7\uff0c\u7f8e\uff0c\u6d6a\u6f2b\uff0c\u7231\uff0c\u8fd9\u4e9b\u624d\u662f\u6211\u4eec\u751f\u5b58\u7684\u539f\u56e0\u3002"}),"\n",(0,o.jsxs)(n.li,{children:["Gather ye rosebuds while ye may, Old time is still a-flying, And this same flower that smiles today, Tomorrow will be dying.\n\u53ca\u65f6\u91c7\u64b7\u4f60\u7684\u82b1\u857e\uff0c\u65e7\u65f6\u5149\u4e00\u53bb\u4e0d\u56de\uff0c\u4eca\u5929\u5c1a\u5728\u5fae\u7b11\u7684\u82b1\u6735\uff0c\u660e\u65e5\u4fbf\u5728\u98ce\u4e2d\u67af\u840e\u3002","\u2b50"]}),"\n",(0,o.jsxs)(n.li,{children:["Carpe diem, seize the day. Boys, ",(0,o.jsx)(n.strong,{children:"make your lives extraordinary."}),"\n\u4eba\u751f\u5c31\u5e94\u8be5\u662f\u5feb\u4e50\u7684\uff0c\u8981\u6293\u4f4f\u6bcf\u4e00\u5929\u3002\u5b69\u5b50\u4eec\uff0c\u8ba9\u4f60\u4eec\u7684\u751f\u6d3b\u53d8\u5f97\u975e\u51e1\u8d77\u6765\u3002"]}),"\n",(0,o.jsx)(n.li,{children:"I want you to find your own walk right now. Your own way of striding, pacing. Any direction. Anything you want. Whether it's proud, whether it's silly, anything.\n\u6211\u5e0c\u671b\u4f60\u4eec\u4e5f\u627e\u5230\u81ea\u5df1\u7684\u8def\uff0c\u627e\u5230\u81ea\u5df1\u7684\u6b65\u4f10\u3001\u6b65\u8c03\uff0c\u4efb\u4f55\u65b9\u5411\uff0c\u4efb\u4f55\u4e1c\u897f\u90fd\u884c\uff0c\u4e0d\u7ba1\u662f\u81ea\u8d1f\u4e5f\u597d\uff0c\u611a\u8822\u4e5f\u597d\uff0c\u4ec0\u4e48\u90fd\u884c\u3002"}),"\n",(0,o.jsx)(n.li,{children:"No matter what anybody tells you, words and ideas can change the world.\n\u4e0d\u7ba1\u522b\u4eba\u600e\u4e48\u8bf4\uff0c\u6587\u5b57\u548c\u601d\u60f3\u7684\u786e\u80fd\u6539\u53d8\u4e16\u754c\u3002"}),"\n",(0,o.jsx)(n.li,{children:"We must constantly look at things in a different way.\n\u6211\u4eec\u5fc5\u987b\u65f6\u523b\u7528\u4e0d\u540c\u773c\u5149\u6765\u770b\u5f85\u4e8b\u7269\u3002"}),"\n",(0,o.jsx)(n.li,{children:"Just when you think you know something,you have to look at it in another way.\n\u5f53\u4f60\u8ba4\u4e3a\u4f60\u77e5\u9053\u67d0\u4ef6\u4e8b\u65f6\uff0c\u5fc5\u987b\u518d\u4ee5\u4e0d\u540c\u89d2\u5ea6\u770b\u5b83"}),"\n",(0,o.jsx)(n.li,{children:"Even though it may seem silly or wrong,you must try!\n\u5373\u4f7f\u90a3\u770b\u6765\u4f3c\u4e4e\u611a\u7b28\u6216\u9519\u8bef\uff0c\u4f60\u4eec\u90fd\u5fc5\u987b\u8bd5\u8bd5\u3002"}),"\n",(0,o.jsx)(n.li,{children:"Now, when you read, don't just consider, what the author thinks, consider what you think.\n\u5f53\u4f60\u9605\u8bfb\u65f6\uff0c\u522b\u53ea\u60f3\u5230\u4f5c\u8005\u7684\u89c1\u89e3\uff0c\u60f3\u60f3\u4f60\u7684\u89c1\u89e3\u3002"}),"\n",(0,o.jsx)(n.li,{children:"Boys, you must strive to find your own voice. Because the longer you wait to begin,the less likely you are to find it at all.\n\u5b69\u5b50\u4eec\uff0c\u4f60\u4eec\u5fc5\u987b\u52aa\u529b\u5bfb\u627e\u81ea\u5df1\u7684\u58f0\u97f3\u3002\u56e0\u4e3a\u4f60\u7b49\u5019\u8d77\u6b65\u7684\u65f6\u95f4\u6108\u957f\uff0c\u4fbf\u6108\u4e0d\u53ef\u80fd\u627e\u5230\u5b83\u3002"}),"\n",(0,o.jsx)(n.li,{children:"Thoreau said, \u201cMost men lead lives of quiet desperation.\u201d\n\u68ad\u7f57\u8bf4\uff0c\u201c\u5927\u591a\u6570\u4eba\u90fd\u751f\u6d3b\u5728\u5e73\u9759\u7684\u7edd\u671b\u4e2d\u3002\u201d"}),"\n",(0,o.jsx)(n.li,{children:"There is a time for daring,and there is a time for caution, and a wise man understanding which is called for.\n\u6709\u65f6\u5019\u8981\u5927\u80c6\uff0c\u6709\u65f6\u5019\u8981\u5c0f\u5fc3\uff0c\u4e00\u4e2a\u806a\u660e\u4eba\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\u8be5\u600e\u4e48\u505a\u3002"}),"\n",(0,o.jsx)(n.li,{children:"Don't be resigned to that. Break out!\n\u522b\u542c\u4efb\u6b64\u4e8b\u53d1\u751f\u3002\u8981\u7a81\u7834\uff01"}),"\n",(0,o.jsx)(n.li,{children:"Robert Frost said, two roads diverged in a wood and I took the one less traveled by, and that has made all the difference.\n\u52b3\u52c3\u4f5b\u6d1b\u65af\u7279\u8bf4\u8fc7\uff0c\u6811\u6797\u91cc\u4e24\u6761\u5c94\u8def\uff0c\u6211\u9009\u62e9\u4eba\u8d70\u5f97\u6bd4\u8f83\u5c11\u7684\u90a3\u6761\u8def\uff0c\u90a3\u91cc\u6709\u5929\u58e4\u4e4b\u522b\u3002"}),"\n",(0,o.jsx)(n.li,{children:"If you noticed, everyone started off with their own stride, their own pace.\n\u4f60\u4eec\u82e5\u662f\u6ce8\u610f\uff0c\u6bcf\u4e2a\u4eba\u90fd\u662f\u4ee5\u81ea\u5df1\u7684\u6b65\u5e45\u884c\u8d70\u3002"}),"\n",(0,o.jsx)(n.li,{children:"The difficulty in maintaining your own beliefs in the face of others.\n\u5728\u4ed6\u4eba\u9762\u524d\u575a\u62a4\u81ea\u5df1\u7684\u4fe1\u5ff5\u662f\u56f0\u96be\u7684\u3002"}),"\n",(0,o.jsx)(n.li,{children:"I would've walked differently.\n\u6211\u672c\u53ef\u4ee5\u8d70\u5f97\u4e0e\u4f17\u4e0d\u540c\u3002"}),"\n",(0,o.jsx)(n.li,{children:"We all have a great need for acceptance. But you must trust that your beliefs are unique,your own. Even though others may think them odd or unpopular. Even though the herd may go,that's bad.\n\u6211\u4eec\u90fd\u975e\u5e38\u9700\u8981\u88ab\u8ba4\u540c\u3002\u4f46\u4f60\u4eec\u5fc5\u987b\u76f8\u4fe1\u81ea\u5df1\u7684\u4fe1\u5ff5\u72ec\u4e00\u65e0\u4e8c\u3002\u7eb5\u4f7f\u522b\u4eba\u53ef\u80fd\u8ba4\u4e3a\u5b83\u5947\u5f02\u6216\u8005\u4e0d\u5165\u6d41\u3002\u7eb5\u4f7f\u5eb8\u4fd7\u7684\u5927\u4f17\u53ef\u80fd\u4f1a\u8bf4\uff0c\u597d\u70c2\u3002"}),"\n",(0,o.jsx)(n.li,{children:"To struggle against great odds, to meet enemies undaunted.\n\u4e0e\u9006\u5883\u4e0d\u5c48\u6297\u4e89\uff0c\u4ee5\u65e0\u754f\u7684\u6c14\u52bf\u9762\u5bf9\u654c\u4eba\u3002"}),"\n",(0,o.jsx)(n.li,{children:"To be a sailor of the world bound for all ports.\n\u505a\u4e16\u754c\u7684\u6c34\u624b\uff0c\u6e38\u904d\u6240\u6709\u7684\u6e2f\u53e3\u3002"}),"\n",(0,o.jsx)(n.li,{children:"Oh,I live to be the ruler of life,not a slave.\n\u6211\u8981\u505a\u751f\u6d3b\u7684\u4e3b\u5bb0\uff0c\u800c\u975e\u5974\u96b6\u3002"}),"\n",(0,o.jsx)(n.li,{children:"Oh, to have life henceforth the poem of new joys. Let it fill your soul.\n\u8ba9\u4eca\u540e\u7684\u751f\u547d\u5982\u559c\u60a6\u7684\u8bd7\u3002\u8ba9\u5b83\u5145\u6ee1\u4f60\u7684\u7075\u9b42\u3002"}),"\n",(0,o.jsx)(n.li,{children:"Because we are food for worms lads. Because,believe or not, each one of us in this room is one day going to stop breathing,turn cold and die.\n\u56e0\u4e3a\u6211\u4eec\u662f\u86c6\u866b\u7684\u98df\u7269\uff0c\u56e0\u4e3a\u4e0d\u7ba1\u4f60\u4fe1\u4e0d\u4fe1\uff0c\u8fd9\u95f4\u623f\u5b50\u91cc\u7684\u6bcf\u4e00\u4e2a\u4eba\uff0c\u603b\u6709\u4e00\u5929\u90fd\u4f1a\u505c\u6b62\u547c\u5438\uff0c\u53d8\u51b7\uff0c\u6b7b\u4ea1\u3002"}),"\n"]})]})}function c(e={}){const{wrapper:n}={...(0,s.R)(),...e.components};return n?(0,o.jsx)(n,{...e,children:(0,o.jsx)(h,{...e})}):h(e)}},28453:(e,n,t)=>{t.d(n,{R:()=>r,x:()=>a});var i=t(96540);const o={},s=i.createContext(o);function r(e){const n=i.useContext(s);return i.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function a(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(o):e.components||o:r(e.components),i.createElement(s.Provider,{value:n},e.children)}}}]);